mapper.delete:
update tb_file set delete_state=1

 id=#{id}
      and name=#{ name }
      and path=#{ path }
      and create_time=#{ create_time }
      and download_times=#{ download_times }
      and file_type=#{ file_type }

mapper.update:
update tb_file
name=#{ name }, path=#{ path }, create_time=#{ create_time }, download_times=#{ download_times }, file_type=#{ file_type }, descn=#{ descn },
id=#{id}

mapper.select:
select id,name,path,create_time,download_times,file_type,descn from tb_file

      and id=#{ id }
      and name=#{ name }
      and path=#{ path }
      and create_time=#{ create_time }
      and download_times=#{ download_times }
      and file_type=#{ file_type }

    order by id desc

select id,name,path,create_time,download_times,file_type,descn from tb_file

      and id=#{ id }
      and name=#{ name }
      and path=#{ path }
      and create_time=#{ create_time }
      and download_times=#{ download_times }
      and file_type=#{ file_type }

limit #{offset},#{rows}

select id,name,path,create_time,download_times,file_type,descn from tb_file

      and id=#{ id }
      and name=#{ name }
      and path=#{ path }
      and create_time=#{ create_time }
      and download_times=#{ download_times }
      and file_type=#{ file_type }

limit 0,1

select count(1) from tb_file

      and id = #{ id }
      and name = #{ name }
      and path = #{ path }
      and create_time = #{ create_time }
      and download_times = #{ download_times }
      and file_type = #{ file_type }
