mapper.delete:
update tb_tiezi_comment set delete_state=1

 id=#{id}
      and tiezi_id=#{ tiezi_id }
      and content=#{ content }
      and user_id=#{ user_id }

mapper.update:
update tb_tiezi_comment
id=#{ id }, tiezi_id=#{ tiezi_id }, content=#{ content }, user_id=#{ user_id },
id=#{id}

mapper.select:
select ttc.id,
   ttc.tiezi_id,
   ttc.content,
   ttc.user_id ,
   ttc.create_time,
   tu.username as username
   from tb_tiezi_comment ttc
   left join tb_user tu on ttc.user_id=tu.id

         and ttc.id=#{ id }
         and ttc.tiezi_id=#{ tiezi_id }
         and ttc.content=#{ content }
         and ttc.user_id=#{ user_id }

select id,tiezi_id,content,user_id from tb_tiezi_comment

      and id=#{ id }
      and tiezi_id=#{ tiezi_id }
      and content=#{ content }
      and user_id=#{ user_id }

limit #{offset},#{rows}

select id,tiezi_id,content,user_id from tb_tiezi_comment

      and id=#{ id }
      and tiezi_id=#{ tiezi_id }
      and content=#{ content }
      and user_id=#{ user_id }

limit 0,1

select count(1) from tb_tiezi_comment

      and id = #{ id }
      and tiezi_id = #{ tiezi_id }
      and content = #{ content }
      and user_id = #{ user_id }
