mapper.delete:
update tb_user set delete_state=1

 id=#{id}
      and username=#{ username }
      and password=#{ password }
      and type=#{ type }

mapper.update:
update tb_user

      id=#{ id },
      username=#{ username },
      password=#{ password },
      type=#{ type },


 id=#{id}
      and username=#{ username }
      and password=#{ password }
      and type=#{ type }

mapper.select:
select id,username,password,type from tb_user

      and id=#{ id }
      and username=#{ username }
      and password=#{ password }
      and type=#{ type }

select id,username,password,type from tb_user

      and id=#{ id }
      and username=#{ username }
      and password=#{ password }
      and type=#{ type }

limit #{offset},#{rows}

select id,username,password,type from tb_user

      and id=#{ id }
      and username=#{ username }
      and password=#{ password }
      and type=#{ type }

limit 0,1

select count(1) from tb_user

      and id = #{ id }
      and username = #{ username }
      and password = #{ password }
      and type = #{ type }
